import random; n=random.randint(1, 100); attempts = 0; print("Guess the number between 1 and 100! Type 'exit' to quit."); [print("Exiting...") or exit() if (u:=input('Your guess: ')).lower()=='exit' else (attempts:=attempts+1, print('Too low!' if (u.isdigit() and (g:=int(u))<n) else 'Too high!' if (u.isdigit() and g>n) else (print(f'Congratulations! You guessed it in {attempts} attempts!') or exit()) if (u.isdigit() and g==n) else 'Please enter a valid number or exit to quit.')) for _ in iter(int, 1)]